import io
import sys
import unittest

def solution():
		Input = lambda : map(int, input().split())
		input()
		Ball = list(set(sorted(list(Input()))))
		n = len(Ball)
		i = 0
		while i < n - 2:
			(a, b, c) = Ball[i:i + 3]
			if c - a <= 2 and a != b != c:
				print('YES')
				exit()
			i += 1
		print('NO')

class TestSolution(unittest.TestCase):
    def run_io_fun(self, input_data):
        backup_stdin = sys.stdin
        backup_stdout = sys.stdout
        try:
            sys.stdin = io.StringIO(input_data)
            output_catcher = io.StringIO()
            sys.stdout = output_catcher

            solution()

            output_catcher.seek(0)
            return output_catcher.read()
        finally:
            sys.stdin = backup_stdin
            sys.stdout = backup_stdout

def make_test_function(input_data, expected):
    def test_function(self):
        actual = self.run_io_fun(input_data)
        self.assertEqual(expected.strip(), actual.strip())
    return test_function

